import * as React from "react"
import { Checkbox as BaseCheckbox, CheckboxProps, themes } from "../../../base"
import { useContext } from "react"
import { ThemeContext } from "styled-components"
import { ControlType, addPropertyControls } from "framer"
import { intentControls, themeControls } from "../propertyControls"
import { centerLayout, OmitTheme } from "../utils"

type Props = OmitTheme<CheckboxProps> &
    Partial<{
        theme: string
        label: string
    }>

const defaults: Props = {
    theme: "light",
    intent: "primary",
    disabled: false,
    label: "Hello world",
    checked: false,
}

export function Checkbox(props: Props = defaults) {
    const themeContext = useContext(ThemeContext)
    const { label, theme, ...rest } = props

    return centerLayout(
        props,
        <BaseCheckbox theme={themeContext || themes[theme]} {...rest}>
            {label}
        </BaseCheckbox>
    )
}

Checkbox.defaultProps = {
    ...defaults,
}

addPropertyControls(Checkbox, {
    ...themeControls(defaults.theme),
    ...intentControls(defaults.intent),
    disabled: {
        type: ControlType.Boolean,
        title: "Disabled",
        defaultValue: defaults.disabled,
    },
    checked: {
        type: ControlType.Boolean,
        title: "Checked",
        defaultValue: defaults.checked,
    },
    label: {
        type: ControlType.String,
        title: "Label",
        defaultValue: defaults.label,
    },
})
